package xyz.shiqihao.common.utils;

import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.util.Arrays;
import java.util.Base64;

import com.esotericsoftware.kryo.Kryo;
import com.esotericsoftware.kryo.Serializer;
import com.esotericsoftware.kryo.io.Input;
import com.esotericsoftware.kryo.io.KryoDataInput;
import com.esotericsoftware.kryo.io.KryoDataOutput;
import com.esotericsoftware.kryo.io.Output;
import org.roaringbitmap.longlong.Roaring64Bitmap;
import xyz.shiqihao.common.IDGenerator;

public class BitmapUtil {
    public static String addAll(long[] ids) {
        Roaring64Bitmap bitmap = new Roaring64Bitmap();
        for (long id : ids) {
            bitmap.add(id);
        }
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        Output output = new Output(baos);
        RoaringSerializer serializer = new RoaringSerializer();
        serializer.write(new Kryo(), output, bitmap);
        output.close();
        return Base64.getEncoder().encodeToString(baos.toByteArray());
    }

    public static long[] parse(String str) {
        ByteArrayInputStream bais = new ByteArrayInputStream(Base64.getDecoder().decode(str));
        Input input = new Input(bais);
        RoaringSerializer serializer = new RoaringSerializer();
        Roaring64Bitmap bitmap = serializer.read(new Kryo(), input, Roaring64Bitmap.class);
        input.close();
        return bitmap.stream().toArray();
    }

    public static void main(String[] args) throws IOException {
        long[] ids = new long[5000];
        for (int i = 0; i < 5000; i++) {
            long id = IDGenerator.gen();
            ids[i] = id;
        }
        // String bitmap = BitmapUtil.addAll(ids);
        // System.out.println(bitmap);

        String str = "";
        System.out.println(Arrays.toString(BitmapUtil.parse(str)));
    }

    private static final class RoaringSerializer extends Serializer<Roaring64Bitmap> {
        @Override
        public void write(Kryo kryo, Output output, Roaring64Bitmap bitmap) {
            try {
                bitmap.serialize(new KryoDataOutput(output));
            } catch (IOException e) {
                e.printStackTrace();
                throw new RuntimeException();
            }
        }

        @Override
        public Roaring64Bitmap read(Kryo kryo, Input input, Class<? extends Roaring64Bitmap> type) {
            Roaring64Bitmap bitmap = new Roaring64Bitmap();
            try {
                bitmap.deserialize(new KryoDataInput(input));
            } catch (IOException e) {
                e.printStackTrace();
                throw new RuntimeException();
            }
            return bitmap;
        }

    }
}
